#include<stdio.h>
int strlen_ur(char[]);
int main()
{
    char a[]="iamthere";
    int v=strlen_ur(a);
    printf("%d",v);
}
int strlen_ur(char d[])
{
    int i,len;
    i=0;
    len=0;
    while(d[i]!='\0')
    {
        len++;
        i++;
    }
    return len;
    
}
    

